using SteamTicketDecrypt.Console;

Console.WriteLine("Hello, World!");
var ticket = "";

var bytes = Enumerable.Range(0, ticket.Length / 2)
    .Select(i => Convert.ToByte(ticket.Substring(i * 2, 2), 16))
    .ToArray();
var parsed = AppTicketParser.ParseAppTicket(bytes);


Console.WriteLine(parsed.SteamID.AccountID);
Console.WriteLine(parsed.AppID);